import {Ping, CreatePing} from "domain/ping/Ping";

export interface PingQuerySystem {
    getPing(name: string): Ping;
}

export function MakePingQuerySystem(): PingQuerySystem {
    return new PingQuerySystemImpl();
}

class PingQuerySystemImpl implements PingQuerySystem {

    public getPing(name: string): Ping  {
        return CreatePing(name).duplicate();
    }

}
