import {InvalidArgumentException} from "domain/Exception";
import assert from "framework/Assert";

export class PositiveInt {
    private _value: number;
    constructor(s: any) {
        assert.nonEmptyString(s, InvalidArgumentException);
        assert.isNonNegativeInteger(s, InvalidArgumentException);
        this._value = parseInt(s, 10);
    }

    get value(): number {
        return this._value;
    }
}

export class SmallPositiveInt extends PositiveInt {
    constructor(s: any) {
        super(s);
        assert(s.length <= 5, InvalidArgumentException);
    }
}

export class TinyPositiveInt extends PositiveInt {
    constructor(s: any) {
        super(s);
        assert(s.length <= 1, InvalidArgumentException);
    }
}
