import {InvalidArgumentException} from "domain/Exception";
const uuidProvider = require("node-uuid");
import assert from "framework/Assert";

export class UUID {
    public value: string;
    constructor(value: string) {
        assert.isUUID(value, InvalidArgumentException);
        this.value = value;
    }
}

export function MakeUUID(value: string): UUID {
    return new UUID(value);
}

export function GenerateRandomUUID(): UUID {
    return MakeUUID(uuidProvider.v4());
}
