export interface Ping {
    duplicate(): Ping;
    toString(): string;
}

export function CreatePing(text: string): Ping {
    return new PingImpl(text);
}

class PingImpl implements Ping {

    private text: string;

    constructor(text: string) {
        this.text = text;
    }

    public toString(): string {
        return "ping " + this.text;
    }

    public duplicate(): Ping {
        this.text = this.text + " " + this.text;
        return this;
    }
}
