const bodyParser = require("body-parser");
import {ExpressMiddleware} from "express/middlewares/ExpressMiddleware";

export default class BodyParser implements ExpressMiddleware {

    private parser: any;

    constructor({
        parser = bodyParser,
        } = {}) {
        this.parser = parser;
    }

    public setup(listener: any) {
        listener.use(this.parser.json({limit: "50mb"}));
        listener.use(this.parser.urlencoded({ extended: true }));
    }
}
