const express = require("express");
import {ExpressMiddleware} from "express/middlewares/ExpressMiddleware";

export interface StaticFolderDefinition {
    folder: string;
    path: string;
}

export default class StaticFolder implements ExpressMiddleware {

    private exp: any;

    constructor(private folder: StaticFolderDefinition, exp?: any) {
        this.exp = exp || express;
    }

    public setup(listener: any) {
        listener.use(this.folder.folder, this.exp.static(this.folder.path));
    }
}
