import {MakePingQuerySystem} from "application/system/PingQuerySystem";
import {Routing} from "express/routes/Routing";


export default class PingRouting implements Routing {

    public handlers() {
        return {
            getPing: (req: any) => MakePingQuerySystem().getPing(req.params.name),
        };
    }

    public fillRoutes(router: any, handlers: any) {
        router.get("/ping/:name", handlers.getPing);
    }
}
