const wait = require("wait.for");

export default function WaitForPromise<T>(promise: Promise<T>): T {
    function stdCallback(cb: (result: T) => void) {
        promise.then(cb.bind(cb, undefined), cb).catch( (e: Error) => { console.log(e); throw e; });
    }

    return wait.for(stdCallback);
}
